// @ts-expect-error import { GraphQLError } from "graphql";
import GET_CATEGORIES_QUERY from "@/graphql/queries/categories/get_all_categories.graphql";
import { GraphQLResponse } from "@/types/apollo"
import { type CategoryItem } from "@/types/Category";
import {print} from "graphql";


type CategoriesResponse = {
    categories?: {
        items?: Array<{
            children?: CategoryItem[];
        }>;
    };
};


const getAllCategories = async (): Promise<CategoryItem[]> => {
    const queryString = print(GET_CATEGORIES_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ query: queryString }),
        });

        if (!res.ok) {
            console.error("Error fetching categories:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<CategoriesResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        const items = data?.categories?.items ?? [];
        return items.flatMap((item) =>
            (item.children ?? []).map((child) => ({
                name: child.name,
                uid: child.uid,
                slug: child.url_key || child.name.toLowerCase().replace(/\s+/g, '-'),
                url_key: child.url_key
            }))
        );
    }
    catch (error) {
        console.error("Error fetching categories:", error);
        return [];
    }
};

export { getAllCategories };

// @ts-expect-error import { GraphQLError } from "graphql";
import GET_CATEGORIES_QUERY from "@/graphql/queries/categories/get_all_categories.graphql";
import { GraphQLResponse } from "@/types/apollo"
import { type CategoryItem } from "@/types/Category";
import {print} from "graphql";


type CategoryTreeItem = {
    uid: string;
    name: string;
    url_key: string;
    level: number;
    path: string;
    children?: CategoryTreeItem[];
};

type CategoriesResponse = {
    categories?: {
        items?: Array<{
            children?: CategoryTreeItem[];
        }>;
    };
};

// Helper function to transform GraphQL category data to our CategoryItem format
const mapCategoryData = (category: CategoryTreeItem): CategoryItem => {
    return {
        name: category.name,
        uid: category.uid,
        url_key: category.url_key,
        path: category.path || '',
        level: category.level || 0,
        children: category.children ? category.children.map(mapCategoryData) : []
    };
};

const getAllCategories = async (): Promise<CategoryItem[]> => {
    const queryString = print(GET_CATEGORIES_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ query: queryString }),
        });

        if (!res.ok) {
            console.error("Error fetching categories:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<CategoriesResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        const items = data?.categories?.items ?? [];
        return items.flatMap((item) =>
            (item.children ?? []).map(mapCategoryData)
        );
    }
    catch (error) {
        console.error("Error fetching categories:", error);
        return [];
    }
};

export { getAllCategories };

import GET_FILTERED_PRODUCTS_QUERY from "@/graphql/queries/products/get_filtered_products.graphql";
import { GraphQLResponse } from "@/types/apollo";
import { ProductItem } from "@/types/product";
import { print } from "graphql";
import { type ProductFilter } from "@/types/product";
import GET_PRODUCTS_BY_CATEGORY_QUERY from "@/graphql/queries/products/get_products_by_category.graphql";

interface ProductsResponse {
    products: {
        total_count: number;
        items: ProductItem[];
    };
}

/**
 * Fetch products by category UID
 */
const getProductsByCategory = async (category_uid: string): Promise<ProductItem[]> => {
    "use cache";
    const queryString = print(GET_PRODUCTS_BY_CATEGORY_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { cat: category_uid },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching products:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        return data?.products?.items ?? [];
    } catch (error) {
        console.error("Error fetching products:", error);
        return [];
    }
};

/**
 * Fetch products by arbitrary Magento filters
 */
const getFilteredProducts = async (filters: ProductFilter): Promise<ProductItem[]> => {
    "use cache";
    const queryString = print(GET_FILTERED_PRODUCTS_QUERY);

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { filter: filters },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching filtered products:", res.statusText);
            return [];
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return [];
        }

        return data?.products?.items ?? [];
    } catch (error) {
        console.error("Error fetching filtered products:", error);
        return [];
    }
};

const getProductByUrlKey = async (url_key: string): Promise<ProductItem | null> => {
    "use cache";
    const queryString = print(GET_FILTERED_PRODUCTS_QUERY);

    const filter: ProductFilter = {
        "url_key":  { eq: url_key }
    }

    try {
        const res = await fetch(process.env.MAGENTO_GRAPHQL_ENDPOINT as string, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                query: queryString,
                variables: { filter: filter },
            }),
        });

        if (!res.ok) {
            console.error("Error fetching product:", res.statusText , "   " ,url_key);
            return null;
        }

        const { data, errors }: GraphQLResponse<ProductsResponse> = await res.json();

        if (errors) {
            console.error("GraphQL errors:", errors);
            return null;
        }

        return data?.products?.items[0] ?? null;
    } catch (error) {
        console.error("Error fetching filtered products:", error);
        return null;
    }
};

export { getProductsByCategory, getFilteredProducts, getProductByUrlKey};

